mapper.select:
select distinct p.id as "id" ,p.proj_name as "projName" from sys_project p ,proj_role_relation pr ,sys_role r ,sys_role_user ru ,sys_user u
where p.id=pr.proj_id and pr.role_id=r.id and r.id=ru.roleId and u.id=ru.userId
and u.username=#{username}

mapper.delete:
		 delete from sys_project
		 where id != 9 and id in

            #{item}

mapper.select:
select * from sys_project sp where 1=1
and sp.id =#{projId}
order by sp.sort
