mapper.select:
select distinct su.id ,su.username ,su.nickname ,su.phone ,su.telephone ,su.email ,su.birthday ,su.sex ,su.status ,su.createTime from sys_user su left join sys_role_user ru on su.id=ru.userId left join sys_role r on r.id=ru.roleId left join proj_role_relation pr on pr.role_id=r.id left join sys_project p on p.id=pr.proj_id where 1=1
and p.id=#{map.projId}
and su.username = #{map.username}
and su.nickname = #{map.nickname}
and su.createTime >= str_to_date(#{map.beginTime},'%Y-%m-%d %H:%i:%s')
and su.comapny = #{map.comapny}
and su.comapnyBelong like '%${map.comapnyBelong}%'
and su.secondConpany like '%${map.secondConpany}%'
and r.id = #{map.roleId}
and su.id = #{map.userId}
order by su.createTime desc

mapper.delete:
delete from sys_user where id in ( select m.id from ( select distinct u.id from sys_user u left join sys_role_user ru on u.id=ru.userId left join sys_role r on r.id=ru.roleId left join proj_role_relation pr on pr.role_id=r.id left join sys_project p on p.id=pr.proj_id
where u.id not in (10,8713) and u.id in
#{item}
and p.id=#{projId}
) m )
